mapper.update:
UPDATE NEWS SET
READCOUNT = READCOUNT + 1
WHERE NEWS_NO = #{newsNo}

mapper.select:
SELECT * FROM NEWS_IMAGE
WHERE NEWS_NO = #{newsNo}
ORDER BY FILE_LEVEL

mapper.insert:
INSERT INTO NEWS_IMAGE SELECT SEQ_NNO.NEXTVAL, A.* FROM(
SELECT #{item.filePath} FILE_PATH, #{item.fileName} FILE_NAME, #{item.fileLevel} FILE_LEVEL, #{item.newsNo} NEWS_NO FROM DUAL
)A

mapper.update:
UPDATE NEWS
SET
NEWS_TITLE=${newsTitle}, PRESS=#{press}, REPORTER=#{reporter}, NEWS_CONTENT=#{newsContent}
WHERE
NEWS_NO
= #{newsNo}

UPDATE NEWS
SET NEWS_STATUS = 'N'
WHERE NEWS_NO = #{newsNo}
